import React from 'react'
import { ArrowLeftOutlined } from '@ant-design/icons'
import { SortableContainer, SortableElement } from 'react-sortable-hoc';


const SortableItem = SortableElement(({ value }) =>
    <li className="col-6 col-md-4 col-lg-3 mt-3 px-2">
        <div style={{ height: '100px', width: '100px', backgroundColor: 'lightblue', display: 'flex', alignItems: 'center', justifyContent: 'center', cursor: 'pointer' }}>
            <h3 style={{ color: 'gray' }}> {value.number} </h3>
        </div>
    </li>
)

const SortableList = SortableContainer(({ items }) => {
    console.log(items)
    return (
        <ul className="row">
            {items.map((value, index) => (
                <SortableItem key={`item-${value.id}`} index={index} value={value} />
            ))}
        </ul>
    )
})

const SortableComponent = (props) => {
    console.log(props.onSortEnd)
    return <SortableList items={props.data} onSortEnd={props.onSortEnd} axis="xy" />;
}

const ProductEdit = (props) => {
    const { data } = props
    const { list } = data

    return (
        <div className='PE-container'>
            <div className='Nav'>
                <h2>Adidas Max Shoe</h2>
            </div>
            <div className='Sec-1'>
                <img className='CoverImage' src='https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg' alt='Cover Image' />
            </div>
            <div className='Sec-2'>
                <div className="grid-list-container">
                    <SortableComponent data={list} onSortEnd={props.orderList} />
                </div>
            </div>
        </div>
    )
}

export default ProductEdit
