import React, { useState } from 'react'
import { ArrowLeftOutlined } from '@ant-design/icons'
import { SortableContainer, SortableElement } from 'react-sortable-hoc';
import { arrayMoveImmutable } from 'array-move';

const SortableItem = SortableElement(({ value }) =>
    <li className="col-6 col-md-4 col-lg-3 mt-3 px-2">
        <div style={{ height: '100px', width: '100px', backgroundColor: 'lightblue', display: 'flex', alignItems: 'center', justifyContent: 'center', cursor: 'pointer' }}>
            <h3 style={{ color: 'gray' }}> {value} </h3>
        </div>
    </li>
)

const SortableList = SortableContainer(({ items }) => {
    console.log(items.items)
    return (
        <div className="row">
            {items.items.map((value, index) => (
                <SortableItem key={`item-${value.id}`} index={index} value={value} />
            ))}
        </div>
    )
})

const ProductEdit = () => {
    const [ArrayList, setArrayList] = useState({
        items: ['Item 1', 'Item 2', 'Item 3', 'Item 4', 'Item 5', 'Item 6','Item 7', 'Item 8', 'Item 9', 'Item 10', 'Item 11', 'Item 12']
        // [
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        //     'https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg',
        // ]
    })


    const onSortEnd = ({ oldIndex, newIndex }) => {
        console.log(oldIndex)
        console.log(newIndex)
        setArrayList(({ items }) => ({
            items: arrayMoveImmutable(items, oldIndex, newIndex),
        }));
    };

    return (
        <div className='PE-container'>
            <div className='Nav'>
                <h2>Adidas Max Shoe</h2>
            </div>
            <div className='Sec-1'>
                <img className='CoverImage' src='https://media.gq-magazine.co.uk/photos/5edfa087310483753e788e82/master/w_1920,c_limit/20200609-adidas-11.jpg' alt='Cover Image' />
            </div>
            <div className='Sec-2'>
                <div className="grid-list-container">
                    <SortableList items={ArrayList} onSortEnd={onSortEnd} />
                </div>
            </div>
        </div>
    )
}

export default ProductEdit
