import {arrayMove} from 'react-sortable-hoc';

const initialState = {
  list: [
    {id: Math.random() , number:1},
    {id: Math.random(), number:2},
    {id: Math.random(), number:3},
    {id: Math.random(), number:4},
    {id: Math.random(), number:5},
    {id: Math.random(), number:6},
    {id: Math.random(), number:7},
    {id: Math.random(), number:8},
    {id: Math.random(), number:9},
    {id: Math.random(), number:10},
    {id: Math.random(), number:11},
    {id: Math.random(), number:12},
    {id: Math.random(), number:13},
    {id: Math.random(), number:14},
    {id: Math.random(), number:15},
  ]
}

export default (state = initialState , action) => {
 switch (action.type) {
  case 'SORTABLE_LIST':
   return {
    ...state,
    list: arrayMove(state.list, action.payload.oldIndex, action.payload.newIndex)
   }
  default:
   return state
 }
}

export const SortableList = (oldIndex, newIndex) => {
  return {
    type: 'SORTABLE_LIST',
    payload: {oldIndex, newIndex},
  }
}
