import { useState, useEffect } from 'react'
import { useHistory } from "react-router-dom";

const Layout = () => {
    const [ProfileEditShow, setProfileEditShow] = useState(true)
    const [WalletShow, setWalletShow] = useState(false)
    const [AdsShow, setAdsShow] = useState(false)
    const [OrderShow, setOrderShow] = useState(false)
    const [AddressShow, setAddressShow] = useState(false)
    const [NotificationShow, setNotificationShow] = useState(false)
    const history = useHistory()


    const onEditProfileClick = () => {
        setProfileEditShow(true)
        setAddressShow(false)
        setWalletShow(false)
        setAdsShow(false)
        setNotificationShow(false)
        setOrderShow(false)
    }
    const onWalletClick = () => {
        setWalletShow(true)
        setAddressShow(false)
        setAdsShow(false)
        setProfileEditShow(false)
        setNotificationShow(false)
        setOrderShow(false)
    }
    const onAdsClick = () => {
        setAdsShow(true)
        setAddressShow(false)
        setProfileEditShow(false)
        setOrderShow(false)
        setWalletShow(false)
        setNotificationShow(false)
    }
    const onAddressClick = () => {
        setAddressShow(true)
        setProfileEditShow(false)
        setWalletShow(false)
        setOrderShow(false)
        setAdsShow(false)
        setNotificationShow(false)
    }
    const onNotificationClick = () => {
        setNotificationShow(true)
        setAddressShow(false)
        setProfileEditShow(false)
        setWalletShow(false)
        setOrderShow(false)
        setAdsShow(false)
    }
    const onOrderClick = () => {
        setOrderShow(true)
        setNotificationShow(false)
        setAddressShow(false)
        setProfileEditShow(false)
        setWalletShow(false)
        setAdsShow(false)
    }

    const OnLogout = () => {
        history.push('/')
    }

    return (
            <div className='Layout-container'>
                <div className='SideBar'>
                    <div className='Option'>
                        <i onClick={onEditProfileClick} class="fas fa-user"> <h4>Edit Profile</h4></i>
                    </div>
                    <div className='Option'>
                        <i onClick={onWalletClick} class="fas fa-wallet"><h4>Wallet</h4></i>
                    </div>
                    <div className='Option'>
                        <i onClick={onAdsClick} class="fas fa-puzzle-piece"><h4>Listings</h4></i>
                    </div>
                    <div className='Option'>
                        <i onClick={onAddressClick} class="fas fa-map"><h4>Address</h4></i>
                    </div>
                    <div className='Option'>
                        <i onClick={onNotificationClick} class="fas fa-bell"><h4>Notifications</h4></i>
                    </div>
                    <div className='Option'>
                        <i onClick={onOrderClick} class="fas fa-layer-group"><h4>Order History</h4></i>
                    </div>
                    <div className='Option'>
                        <i onClick={OnLogout} class="fas fa-sign-out-alt"><h4>Logout</h4></i>
                    </div>
                </div>
                <div className='Content-div'>
                    {/* {ProfileEditShow && <ProfileEdit /> }
                    {WalletShow && <Wallet /> }
                    {AdsShow && <Ads /> }
                    {AddressShow && <Address /> }
                    {NotificationShow && <Notification /> }
                    {OrderShow && <Order /> } */}
                </div>
            </div>
    )
}

export default Layout
