import { useState, useEffect } from 'react'
import { useHistory } from "react-router-dom";
import { DashboardOutlined, UserOutlined, FileDoneOutlined , BellOutlined ,NotificationOutlined, LogoutOutlined, DollarOutlined } from '@ant-design/icons'
import ProfileEdit from '../Components/ProfileEdit/ProfileEdit';

const Layout = () => {
    const [ProfileEditShow, setProfileEditShow] = useState(false)
    const [WalletShow, setWalletShow] = useState(false)
    const [AdsShow, setAdsShow] = useState(false)
    const [OrderShow, setOrderShow] = useState(false)
    const [AddressShow, setAddressShow] = useState(false)
    const [NotificationShow, setNotificationShow] = useState(false)
    const history = useHistory()


    const onEditProfileClick = () => {
        setProfileEditShow(true)
        setAddressShow(false)
        setWalletShow(false)
        setAdsShow(false)
        setNotificationShow(false)
        setOrderShow(false)
    }
    const onWalletClick = () => {
        setWalletShow(true)
        setAddressShow(false)
        setAdsShow(false)
        setProfileEditShow(false)
        setNotificationShow(false)
        setOrderShow(false)
    }
    const onAdsClick = () => {
        setAdsShow(true)
        setAddressShow(false)
        setProfileEditShow(false)
        setOrderShow(false)
        setWalletShow(false)
        setNotificationShow(false)
    }
    const onAddressClick = () => {
        setAddressShow(true)
        setProfileEditShow(false)
        setWalletShow(false)
        setOrderShow(false)
        setAdsShow(false)
        setNotificationShow(false)
    }
    const onNotificationClick = () => {
        setNotificationShow(true)
        setAddressShow(false)
        setProfileEditShow(false)
        setWalletShow(false)
        setOrderShow(false)
        setAdsShow(false)
    }
    const onOrderClick = () => {
        setOrderShow(true)
        setNotificationShow(false)
        setAddressShow(false)
        setProfileEditShow(false)
        setWalletShow(false)
        setAdsShow(false)
    }

    const OnLogout = () => {
        history.push('/')
    }

    return (
        <div className='Layout-container'>
            <div className='SideBar'>
                <div className='Option'>
                    <DashboardOutlined  className='icon' />
                    <h4>Dashboard</h4>
                </div>
                <div className='Option'>
                    <UserOutlined onClick={onEditProfileClick} className='icon' />
                    <h4>Edit Profile</h4>
                </div>
                <div className='Option'>
                    <NotificationOutlined className='icon' />
                    <h4>Notifications</h4>
                </div>
                <div className='Option'>
                    <DollarOutlined className='icon' />
                    <h4>Earning</h4>
                </div>
                <div className='Option'>
                <BellOutlined className='icon' />
                    <h4>Notifications</h4>
                </div>
                <div className='Option'>
                <FileDoneOutlined className='icon' />
                    <h4>Payment</h4>
                </div>
                <div className='Option'>
                    <LogoutOutlined className='icon' />
                    <h4>Logout</h4>
                </div>
            </div>
            <div className='Content-div'>
                {ProfileEditShow && <ProfileEdit /> }
                    {/* {WalletShow && <Wallet /> }
                    {AdsShow && <Ads /> }
                    {AddressShow && <Address /> }
                    {NotificationShow && <Notification /> }
                    {OrderShow && <Order /> } */}
            </div>
        </div>
    )
}

export default Layout
